import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { MapIcon, ExclamationTriangleIcon, DocumentTextIcon } from '@heroicons/react/24/outline';

export default function Home() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700 flex items-center justify-center">
        <div className="bg-white/10 backdrop-blur-sm p-8 rounded-lg flex flex-col items-center">
          <div className="w-16 h-16 border-4 border-white border-t-transparent rounded-full animate-spin mb-4"></div>
          <p className="text-white text-lg">Welcome to Namma Suraksha...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-800 to-pink-700">
      <div className="container mx-auto px-4 py-8 flex flex-col items-center justify-center min-h-screen">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-6 text-center opacity-0 animate-fade-in">
          Welcome to Namma Suraksha
        </h1>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 w-full max-w-5xl mb-6">
          <button
            onClick={() => navigate('/map')}
            className="group bg-white/10 backdrop-blur-sm hover:bg-white/20 p-4 rounded-lg transition-all duration-300 flex flex-col items-center text-center border border-white/20 hover:border-white/40 transform hover:-translate-y-1 opacity-0 animate-fade-in-up"
            style={{ animationDelay: '200ms' }}
          >
            <MapIcon className="h-10 w-10 text-white mb-3 group-hover:scale-110 transition-transform" />
            <h2 className="text-lg font-semibold text-white mb-1">Crime Map üó∫Ô∏è</h2>
            <p className="text-white/80 text-xs">View crime hotspots and stay informed</p>
          </button>

          <button
            onClick={() => navigate('/incidents')}
            className="group bg-white/10 backdrop-blur-sm hover:bg-white/20 p-4 rounded-lg transition-all duration-300 flex flex-col items-center text-center border border-white/20 hover:border-white/40 transform hover:-translate-y-1 opacity-0 animate-fade-in-up"
            style={{ animationDelay: '400ms' }}
          >
            <DocumentTextIcon className="h-10 w-10 text-white mb-3 group-hover:scale-110 transition-transform" />
            <h2 className="text-lg font-semibold text-white mb-1">Reported Incidents üìã</h2>
            <p className="text-white/80 text-xs">View all reported incidents</p>
          </button>

          <button
            onClick={() => navigate('/report')}
            className="group bg-white/10 backdrop-blur-sm hover:bg-white/20 p-4 rounded-lg transition-all duration-300 flex flex-col items-center text-center border border-white/20 hover:border-white/40 transform hover:-translate-y-1 opacity-0 animate-fade-in-up"
            style={{ animationDelay: '600ms' }}
          >
            <ExclamationTriangleIcon className="h-10 w-10 text-white mb-3 group-hover:scale-110 transition-transform" />
            <h2 className="text-lg font-semibold text-white mb-1">Report Incident ‚ö†Ô∏è</h2>
            <p className="text-white/80 text-xs">Report suspicious activities</p>
          </button>
        </div>
        <p className="text-white/60 text-center text-sm max-w-2xl opacity-0 animate-fade-in" style={{ animationDelay: '800ms' }}>
          Together, we can make our community safer. Report incidents, stay informed, and help create a secure environment for everyone.
        </p>
      </div>
    </div>
  );
}
